import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    Box,
    Button,
    CircularProgress,
    List,
    ListItem,
    ListItemText,
    Typography
} from '@mui/material';
import axios from 'axios';

export const HubSpotIntegration = ({ user, org, integrationParams, setIntegrationParams }) => {
    const [isConnected, setIsConnected] = useState(false);
    const [isConnecting, setIsConnecting] = useState(false);
    const [hubspotItems, setHubspotItems] = useState([]);
    const [isFetching, setIsFetching] = useState(false);
    const navigate = useNavigate();

    // Function to open OAuth in same window (HubSpot pattern)
    const handleConnectClick = async () => {
        try {
            setIsConnecting(true);
            const formData = new FormData();
            formData.append('user_id', user);
            formData.append('org_id', org);
            const response = await axios.post(`http://localhost:8000/integrations/hubspot/authorize`, formData);
            window.location.href = response.data; // HubSpot redirects back to your callback URL
        } catch (e) {
            setIsConnecting(false);
            alert(e?.response?.data?.detail);
        }
    };

    // Check for successful connection after redirect
    useEffect(() => {
        const urlParams = new URLSearchParams(window.location.search);
        const success = urlParams.get('success');
        
        if (success === 'hubspot') {
            const completeConnection = async () => {
                try {
                    const formData = new FormData();
                    formData.append('user_id', user);
                    formData.append('org_id', org);
                    const response = await axios.post(`http://localhost:8000/integrations/hubspot/credentials`, formData);
                    const credentials = response.data;
                    
                    if (credentials) {
                        setIsConnected(true);
                        setIntegrationParams(prev => ({ ...prev, credentials, type: 'HubSpot' }));
                        navigate('/integrations', { replace: true });
                    }
                } catch (e) {
                    alert(e?.response?.data?.detail);
                } finally {
                    setIsConnecting(false);
                }
            };
            completeConnection();
        }
    }, [navigate, user, org, setIntegrationParams]);

    const fetchHubSpotItems = async () => {
        setIsFetching(true);
        try {
            const response = await axios.get('http://localhost:8000/integrations/hubspot/items');
            setHubspotItems(response.data.items || []);
        } catch (e) {
            alert(e?.response?.data?.detail);
        } finally {
            setIsFetching(false);
        }
    };

    const handleDisconnect = async () => {
        try {
            setIsConnecting(true);
            await axios.delete('http://localhost:8000/integrations/hubspot/credentials');
            setIsConnected(false);
            setHubspotItems([]);
            setIntegrationParams(prev => ({ ...prev, credentials: null }));
        } catch (e) {
            alert(e?.response?.data?.detail);
        } finally {
            setIsConnecting(false);
        }
    };

    // Initialize connection status
    useEffect(() => {
        setIsConnected(integrationParams?.credentials ? true : false);
    }, [integrationParams?.credentials]);

    return (
        <Box sx={{ mt: 2 }}>
            <Typography variant="h6">HubSpot Integration</Typography>
            <Box display="flex" alignItems="center" justifyContent="center" sx={{ mt: 2 }}>
                <Button
                    variant="contained"
                    onClick={isConnected ? handleDisconnect : handleConnectClick}
                    color={isConnected ? 'success' : 'primary'}
                    disabled={isConnecting}
                    sx={{
                        pointerEvents: isConnecting ? 'none' : 'auto',
                        mr: 2
                    }}
                >
                    {isConnected 
                        ? 'Disconnect HubSpot' 
                        : isConnecting 
                            ? <CircularProgress size={20} /> 
                            : 'Connect to HubSpot'}
                </Button>
                
                {isConnected && (
                    <Button
                        variant="outlined"
                        onClick={fetchHubSpotItems}
                        disabled={isFetching}
                    >
                        {isFetching ? <CircularProgress size={20} /> : 'Fetch Contacts'}
                    </Button>
                )}
            </Box>

            {isConnected && hubspotItems.length > 0 && (
                <List sx={{ mt: 2 }}>
                    {hubspotItems.map((item, index) => (
                        <ListItem key={index}>
                            <ListItemText
                                primary={item.name || 'No name'}
                                secondary={
                                    <>
                                        {item.description || 'No description'}
                                        <br />
                                        {item.metadata?.company && `Company: ${item.metadata.company}`}
                                        {item.metadata?.phone && ` | Phone: ${item.metadata.phone}`}
                                    </>
                                }
                            />
                        </ListItem>
                    ))}
                </List>
            )}

            {isConnected && !isFetching && hubspotItems.length === 0 && (
                <Typography sx={{ mt: 2 }}>No contacts found. Click "Fetch Contacts" to load data.</Typography>
            )}
        </Box>
    );
};

export default HubSpotIntegration;
