import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Card, List, message, Spin } from 'antd';
import { LoadingOutlined, LinkOutlined } from '@ant-design/icons';
import { useAuth } from '../contexts/AuthContext';

export const HubSpotIntegration = ({ api, user, org, integrationParams, setIntegrationParams }) => {
    const { currentUser } = useAuth();
    const navigate = useNavigate();
    const [isConnected, setIsConnected] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [hubspotItems, setHubspotItems] = useState([]);
    const [isFetching, setIsFetching] = useState(false);
    const [isDisconnecting, setIsDisconnecting] = useState(false);

    // Check connection status when user or credentials change
    useEffect(() => {
        const checkConnection = async () => {
            if (!integrationParams?.credentials) {
                setIsConnected(false);
                setIsLoading(false);
                return;
            }

            try {
                const response = await api.get('/integrations/hubspot/credentials');
                setIsConnected(response.data?.connected || false);
            } catch (error) {
                setIsConnected(false);
                console.error('Connection check failed:', error);
            } finally {
                setIsLoading(false);
            }
        };

        if (currentUser) {
            checkConnection();
        }
    }, [currentUser, api, integrationParams?.credentials]);

    const handleConnect = () => {
        window.location.href = '/integrations/hubspot/authorize';
    };

    const handleDisconnect = async () => {
        setIsDisconnecting(true);
        try {
            await api.delete('/integrations/hubspot/credentials');
            setIsConnected(false);
            setHubspotItems([]);
            setIntegrationParams(prev => ({ ...prev, credentials: null }));
            message.success('Successfully disconnected from HubSpot');
        } catch (error) {
            const errorMsg = error.response?.data?.detail || error.message;
            message.error(`Failed to disconnect: ${errorMsg}`);
        } finally {
            setIsDisconnecting(false);
        }
    };

    const fetchHubSpotItems = async () => {
        setIsFetching(true);
        try {
            const response = await api.get('/integrations/hubspot/items');
            setHubspotItems(response.data.items || []);
            message.success(`Fetched ${response.data.items?.length || 0} contacts`);
        } catch (error) {
            const errorMsg = error.response?.data?.detail || error.message;
            message.error(`Failed to fetch contacts: ${errorMsg}`);
            console.error('HubSpot fetch error:', error);
        } finally {
            setIsFetching(false);
        }
    };

    // Handle OAuth callback success
    useEffect(() => {
        const urlParams = new URLSearchParams(window.location.search);
        const success = urlParams.get('success');

        if (success === 'hubspot') {
            message.success('Successfully connected to HubSpot!');
            setIsConnected(true);
            navigate('/integrations', { replace: true });
        }
    }, [navigate]);

    if (isLoading) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', padding: '24px' }}>
                <Spin indicator={<LoadingOutlined style={{ fontSize: 24 }} spin />} />
            </div>
        );
    }

    return (
        <Card
            title="HubSpot Integration"
            actions={[
                isConnected ? (
                    <>
                        <Button 
                            key="fetch" 
                            onClick={fetchHubSpotItems} 
                            loading={isFetching}
                            disabled={isDisconnecting}
                        >
                            Fetch Contacts
                        </Button>
                        <Button 
                            key="disconnect" 
                            danger 
                            onClick={handleDisconnect} 
                            loading={isDisconnecting}
                            disabled={isFetching}
                        >
                            Disconnect
                        </Button>
                    </>
                ) : (
                    <Button
                        key="connect"
                        type="primary"
                        onClick={handleConnect}
                        icon={<LinkOutlined />}
                        disabled={isLoading}
                    >
                        Connect HubSpot Account
                    </Button>
                )
            ]}
        >
            {isConnected ? (
                <>
                    <p>Your account is connected to HubSpot.</p>
                    {hubspotItems.length > 0 ? (
                        <List
                            itemLayout="horizontal"
                            dataSource={hubspotItems}
                            renderItem={(item) => (
                                <List.Item>
                                    <List.Item.Meta
                                        title={item.name || 'No name'}
                                        description={item.description || 'No description'}
                                    />
                                    <div>
                                        {item.metadata?.company && <span>Company: {item.metadata.company}</span>}
                                        {item.metadata?.phone && <span> | Phone: {item.metadata.phone}</span>}
                                    </div>
                                </List.Item>
                            )}
                        />
                    ) : (
                        <p>No contacts found. Click "Fetch Contacts" to load data.</p>
                    )}
                </>
            ) : (
                <p>Connect your HubSpot account to access CRM data.</p>
            )}
        </Card>
    );
};

export default HubSpotIntegration;
