import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, CircularProgress, List, ListItem, ListItemText, Typography, Alert, Divider } from '@mui/material';
import axios from 'axios';

export const HubSpotIntegration = ({ user, org, integrationParams, setIntegrationParams }) => {
    const [state, setState] = useState({
        isConnected: !!integrationParams?.credentials,
        isLoading: false,
        items: [],
        error: null
    });

    const navigate = useNavigate();
    const API_BASE = 'http://localhost:8000'; // Ensure this matches your backend

    // Verify these endpoints match your FastAPI router
    const ENDPOINTS = {
        authorize: `${API_BASE}/integrations/hubspot/authorize`,
        credentials: `${API_BASE}/integrations/hubspot/credentials`,
        items: `${API_BASE}/integrations/hubspot/items`
    };

    useEffect(() => {
        const checkConnection = async () => {
            if (!integrationParams?.credentials) return;
            
            try {
                const { data } = await axios.get(ENDPOINTS.credentials, { 
                    params: { user_id: user, org_id: org } 
                });
                setState(prev => ({ ...prev, isConnected: data?.connected || false }));
            } catch (error) {
                console.error('Connection check failed:', error);
                setState(prev => ({ ...prev, isConnected: false }));
            }
        };
        checkConnection();
    }, [user, org, integrationParams]);

    const handleAuth = async (action) => {
        setState(prev => ({ ...prev, isLoading: true, error: null }));
        
        try {
            if (action === 'connect') {
                const { data } = await axios.post(ENDPOINTS.authorize, 
                    { user_id: user, org_id: org },
                    { headers: { 'Content-Type': 'application/json' } }
                );

                if (!data?.url) throw new Error('Authorization URL missing');
                localStorage.setItem('hubspot_connection', JSON.stringify({ user, org }));
                window.location.href = data.url;
                return;
            }

            if (action === 'disconnect') {
                await axios.delete(ENDPOINTS.credentials, {
                    data: { user_id: user, org_id: org },
                    headers: { 'Content-Type': 'application/json' }
                });
                setState(prev => ({ ...prev, isConnected: false, items: [] }));
                setIntegrationParams(prev => ({ ...prev, credentials: null }));
                return;
            }
        } catch (error) {
            const errorMsg = error.response?.data?.detail || 
                          error.response?.data?.message || 
                          error.message || 
                          'Operation failed';
            setState(prev => ({ ...prev, error: errorMsg }));
        } finally {
            setState(prev => ({ ...prev, isLoading: false }));
        }
    };

    const fetchItems = async () => {
        setState(prev => ({ ...prev, isLoading: true, error: null }));
        
        try {
            const { data } = await axios.get(ENDPOINTS.items, {
                params: { user_id: user, org_id: org }
            });
            setState(prev => ({ ...prev, items: data?.items || [] }));
        } catch (error) {
            const errorMsg = error.response?.data?.detail || 
                          error.response?.data?.message || 
                          error.message || 
                          'Failed to fetch contacts';
            setState(prev => ({ ...prev, error: errorMsg }));
        } finally {
            setState(prev => ({ ...prev, isLoading: false }));
        }
    };

    useEffect(() => {
        const completeConnection = async () => {
            const params = new URLSearchParams(window.location.search);
            const code = params.get('code');
            const error = params.get('error');

            if (error) {
                setState(prev => ({ ...prev, error: `Authorization failed: ${error}` }));
                return;
            }

            if (code) {
                try {
                    const connection = JSON.parse(localStorage.getItem('hubspot_connection') || '{}');
                    if (connection.user !== user || connection.org !== org) {
                        throw new Error('User mismatch during authorization');
                    }

                    const { data } = await axios.post(ENDPOINTS.credentials, {
                        user_id: user, 
                        org_id: org,
                        code
                    }, {
                        headers: { 'Content-Type': 'application/json' }
                    });

                    if (!data?.access_token) throw new Error('Invalid credentials received');
                    
                    setState(prev => ({ ...prev, isConnected: true }));
                    setIntegrationParams(prev => ({
                        ...prev,
                        credentials: data,
                        type: 'HubSpot'
                    }));
                    localStorage.removeItem('hubspot_connection');
                    navigate('/integrations', { replace: true });

                } catch (error) {
                    const errorMsg = error.response?.data?.detail || 
                                  error.response?.data?.message || 
                                  error.message || 
                                  'Connection failed';
                    setState(prev => ({ ...prev, error: errorMsg }));
                }
            }
        };

        completeConnection();
    }, [navigate, user, org, setIntegrationParams]);

    return (
        <Box sx={{ mt: 2, p: 2, border: '1px solid #ddd', borderRadius: 2 }}>
            <Typography variant="h6" gutterBottom>HubSpot Integration</Typography>
            
            {state.error && (
                <Alert severity="error" sx={{ mb: 2 }} onClose={() => setState(prev => ({ ...prev, error: null }))}>
                    {state.error.includes('404') ? (
                        'Endpoint not found. Please check backend routes.'
                    ) : (
                        state.error
                    )}
                </Alert>
            )}

            <Box display="flex" alignItems="center" gap={2} sx={{ mb: 3 }}>
                <Button
                    variant="contained"
                    color={state.isConnected ? 'success' : 'primary'}
                    onClick={() => handleAuth(state.isConnected ? 'disconnect' : 'connect')}
                    disabled={state.isLoading}
                    startIcon={state.isLoading && <CircularProgress size={20} color="inherit" />}
                >
                    {state.isConnected ? 'Disconnect' : state.isLoading ? 'Processing...' : 'Connect'}
                </Button>
                
                {state.isConnected && (
                    <Button
                        variant="outlined"
                        onClick={fetchItems}
                        disabled={state.isLoading}
                        startIcon={state.isLoading && <CircularProgress size={20} />}
                    >
                        {state.isLoading ? 'Fetching...' : 'Fetch Contacts'}
                    </Button>
                )}
            </Box>

            {state.isConnected && (
                <>
                    <Divider sx={{ my: 2 }} />
                    <Typography variant="subtitle1" gutterBottom>HubSpot Data</Typography>
                    
                    {state.items.length > 0 ? (
                        <List dense sx={{ maxHeight: 300, overflow: 'auto' }}>
                            {state.items.map((item, index) => (
                                <ListItem key={index} divider>
                                    <ListItemText
                                        primary={item.name || 'Unnamed Contact'}
                                        secondary={
                                            <>
                                                {item.description || 'No description'}
                                                <br />
                                                {item.metadata?.company && `Company: ${item.metadata.company}`}
                                                {item.metadata?.phone && ` • Phone: ${item.metadata.phone}`}
                                            </>
                                        }
                                    />
                                </ListItem>
                            ))}
                        </List>
                    ) : (
                        <Typography variant="body2" color="text.secondary">
                            {state.isLoading ? 'Loading...' : 'No contacts found'}
                        </Typography>
                    )}
                </>
            )}
        </Box>
    );
};

export default HubSpotIntegration;
